mapper.select:
SELECT `work`.*
FROM user_course,`work`
WHERE user_course.courseId=`work`.courseId AND userId = #{userId}

SELECT * FROM `work`


        and courseId = #{courseId}


        and name  LIKE concat('%',#{name},'%')

SELECT * FROM `work`


       and id = #{workId}


        and courseId=#{courseId}


        and chapterId=#{chapterId}

SELECT *
FROM user_work


        AND workId = #{workId}


        AND userId = #{userId}


        AND workId IN (
        SELECT id
        FROM `work`
        WHERE courseId=#{courseId} AND chapterId=#{chapterId}
        )

SELECT workId,userId,upFilePath,`comment`
FROM user_work
WHERE workId=#{workId} AND userId=#{userId}

mapper.update:
UPDATE user_work
SET score = #{score},
feedback =  #{feedback}
WHERE workId = #{workId} AND userId = #{userId}

 UPDATE user_work
 SET upFilePath = #{upFilePath},
comment =  #{comment}
 WHERE workId = #{workId} AND userId = #{userId}
